import{_ as t,c,o as i,j as n,a as h}from"./chunks/framework.NAYRmF77.js";const x=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"in-the-brain/13-chemical.md","filePath":"in-the-brain/13-chemical.md"}'),g={name:"in-the-brain/13-chemical.md"},a=n("h2",{id:"_13-moi-thu-đeu-la-phan-ung-hoa-hoc",tabindex:"-1"},[h("13. Mọi thứ đều là phản ứng hóa học "),n("a",{class:"header-anchor",href:"#_13-moi-thu-đeu-la-phan-ung-hoa-hoc","aria-label":'Permalink to "13. Mọi thứ đều là phản ứng hóa học"'},"​")],-1),u=n("p",null,"Mọi suy nghĩ, dù là ý tưởng cơ bản hay quyết định cao cấp, thậm chí là những cảm xúc không thể giải thích được, thực chất đều là những phản ứng hóa học giữa các chất hóa học được tiết ra trong cơ quan vật lý của não bộ... Vì vậy, nhiều khi, nhiều suy nghĩ thực sự khá ngẫu nhiên và thoáng qua.",-1),o=n("p",null,"Khi chúng ta tỉnh dậy từ giấc mơ, nếu giữ nguyên tư thế và nhắm mắt tiếp tục ngủ, rất có thể chúng ta sẽ trở lại giấc mơ gốc (hoặc ít nhất là giấc mơ tương tự) — nhưng nếu thay đổi tư thế, chúng ta sẽ không thể quay lại. Điều này là vì việc thay đổi tư thế sẽ làm thay đổi vị trí của các chất hóa học trong não bộ...",-1),e=n("p",null,"Trong cuộc sống hàng ngày, khi một suy nghĩ không thoải mái xâm nhập vào đầu chúng ta, chúng ta thường vô thức lắc đầu mạnh mẽ, hy vọng có thể loại bỏ nó — điều này không phải là vô lý, vì hành động này thực sự có hiệu quả, vì các chất hóa học trong não bộ, ngay cả sự thay đổi nhỏ trong vị trí cũng có thể dẫn đến phản ứng hóa học khác biệt... Vì vậy, lắc đầu, phản ứng hóa học thay đổi, suy nghĩ thực sự có thể thay đổi hoặc biến mất.",-1),l=n("p",null,"Tương tự, khi chúng ta cảm thấy căng thẳng, cần phải hít thở sâu — điều này cũng có thể được giải thích là phản ứng hóa học trong não bộ trở nên quá dữ dội, vì vậy cần phải bổ sung nhiều năng lượng, oxy và glucose, nếu không thì không thể tiếp tục làm việc bình thường.",-1),s=n("p",null,[h("Phản ứng hóa học có thể ngẫu nhiên và không ổn định, vì vậy, việc "),n("strong",null,"đi theo cảm giác"),h(" thực sự là rất đáng sợ. Đây chính là lý do tại sao chúng ta cần thói quen xem xét nghiêm túc từng suy nghĩ của chính mình. Chúng ta không muốn và không thể sống quá ngẫu nhiên và không ổn định. Chúng ta đấu tranh để tìm hướng đi và duy trì hướng đi, chính là để thoát khỏi sự ngẫu nhiên, tìm kiếm sự ổn định.")],-1),r=n("p",null,"Các phản ứng hóa học trong não bộ chủ yếu dẫn đến các phát xạ vật lý giữa các neuron, làm chúng kết nối và giao tiếp với nhau, cuối cùng điều phối các cơ quan cơ thể theo một cách nào đó. Một chuỗi phản ứng hóa học và các suy nghĩ tương ứng của nó, nếu xảy ra lặp đi lặp lại, sẽ được củng cố, cho đến khi các cơ quan vật lý hình thành quy trình làm việc cố định. Thực tế, mọi người có quá nhiều suy nghĩ chưa được tự kiểm duyệt và thói quen tương ứng với chúng.",-1),m=[a,u,o,e,l,s,r];function p(y,v,d,_,k,b){return i(),c("div",null,m)}const f=t(g,[["render",p]]);export{x as __pageData,f as default};
