import{_ as n,c as t,o as h,a5 as c,ag as i}from"./chunks/framework.GA2e1Mwe.js";const d=JSON.parse('{"title":"1. Tự học hướng dẫn","description":"","frontmatter":{"0":"o","1":"u","2":"t","3":"l","4":"i","5":"n","6":"e","7":" ","8":"[","9":"2","10":",","11":" ","12":"3","13":"]"},"headers":[],"relativePath":"self-training/00-intro.md","filePath":"self-training/00-intro.md"}'),g={name:"self-training/00-intro.md"},o=c('<h1 id="_1-tu-hoc-huong-dan" tabindex="-1">1. Tự học hướng dẫn <a class="header-anchor" href="#_1-tu-hoc-huong-dan" aria-label="Permalink to &quot;1. Tự học hướng dẫn&quot;">​</a></h1><p>Trong năm huấn luyện và rèn luyện này, chỉ có hai nhiệm vụ chính: <strong>định hình âm thanh</strong> và <strong>mở rộng trí nhớ</strong>. Mục tiêu chính trong năm này là phải dành ít nhất ba giờ mỗi ngày và tập trung ít nhất một nghìn giờ trong năm.</p><p>Đừng nghĩ rằng thành quả quan trọng nhất chỉ là <strong>biến tiếng Anh thành ngôn ngữ chính</strong>... Trong quá trình này, điều chúng ta tích lũy và đạt được không chỉ là đáp ứng mục tiêu nhiệm vụ, mà còn là (có lẽ là lần đầu tiên trong đời nhiều người) <strong>trải nghiệm tự học hoàn chỉnh</strong>.</p><p>Và không chỉ vậy, còn có điều quan trọng hơn nữa — trải nghiệm tự học hoàn chỉnh này sẽ chắc chắn thay đổi cách nhìn của một người đối với toàn bộ giáo dục, từ đó ảnh hưởng đến các lựa chọn và thay đổi cả số phận của một người hoặc cả gia đình.</p><p>Là một tác giả, tôi không thể giới hạn chính mình chỉ trong lĩnh vực giảng dạy tiếng Anh. Lí thuyết cho thấy, chúng ta có thể học được mọi thứ trên thế giới bằng cách tương tự, miễn là cảm thấy cần thiết.</p><p><img src="'+i+'" alt="Don Quixote"></p><p>Theo một cách nhìn khác, đây là cuộc chiến của tôi với toàn bộ hệ thống giáo dục trên trái đất, vì lợi ích của con cái — hay có thể nói là một câu chuyện của Don Quixote, một kẻ điên cố gắng đánh bại những cánh cối xay gió, cho dù người ta nghĩ gì đi nữa.</p>',7),a=[o];function r(e,s,m,l,u,_){return h(),t("div",null,a)}const y=n(g,[["render",r]]);export{d as __pageData,y as default};
