import{_ as a,c as s,o as e,a5 as l}from"./chunks/framework.Dg_FYKo7.js";const f=JSON.parse('{"title":"2.3.1. Cấu trúc (Structure of a syllable)","description":"","frontmatter":{},"headers":[],"relativePath":"sounds-of-american-english/2.3.1-structure.md","filePath":"sounds-of-american-english/2.3.1-structure.md"}'),n={name:"sounds-of-american-english/2.3.1-structure.md"},p=l('<h1 id="_2-3-1-cau-truc-structure-of-a-syllable" tabindex="-1">2.3.1. Cấu trúc (Structure of a syllable) <a class="header-anchor" href="#_2-3-1-cau-truc-structure-of-a-syllable" aria-label="Permalink to &quot;2.3.1. Cấu trúc (Structure of a syllable)&quot;">​</a></h1><p><strong>Từ vựng tiếng Anh bao gồm ít nhất một âm tiết</strong>. Một số từ phổ biến nhất trong tiếng Anh như <em>a/an</em>, <em>the</em>, <em>of</em>, <em>to</em>, <em>for</em>... đều là từ một âm tiết. Có những từ rất dài, được cấu thành từ nhiều âm tiết. Trong từ điển tiếng Anh, từ dài nhất có tổng cộng 19 âm tiết và 45 chữ cái:</p><blockquote><p><em><a href="https://en.wikipedia.org/wiki/Pneumonoultramicroscopicsilicovolcanoconiosis" target="_blank" rel="noreferrer">Pneumonoultramicroscopicsilicovolcanoconiosis</a></em> <span class="speak-word-inline" data-audio-us-female="/audios/En-us-pneumonoultramicroscopicsilicovolcanoconiosis.ogg.mp3"></span></p><p><span class="pho alt">ˌnumənəʊˌəltrəˌmaɪkrəˌskɑpɪkˌsɪləkəʊvɑlˌkeɪnəʊˌkəʊniˈəʊsəs</span></p></blockquote><p>Trong một âm tiết, có và chỉ có một nguyên âm (được gọi là <em>nucleus</em>), đây cũng là phần vần của âm tiết (<em>rhyme</em>). Âm tiết được trung tâm quanh nguyên âm, có thể có tới ba phụ âm tạo thành các tổ hợp trước và sau nguyên âm.</p><p>In a syllable, there is <em>only</em> one vowel, known as the <em>nucleus</em>, which forms the <em>rhyme</em> of the syllable. This vowel is at the core of the syllable, and it can be surrounded by up to three consonants.</p><p>Cấu trúc âm tiết trong tiếng Anh chủ yếu có các dạng sau đây (trong đó, <span class="pho">c</span> đại diện cho phụ âm, và <span class="pho">v</span> đại diện cho nguyên âm):</p><p>In English, syllables can generally be made up of the following patterns (where <span class="pho">c</span> stands for consonant and <span class="pho">v</span> stands for vowel):</p><blockquote><ul><li><span class="pho">v</span> - <em>awe</em> <span class="pho alt">ɑː</span> <span class="speak-word-inline" data-audio-us-male="/audios/us/awe-us-male.mp3" data-audio-us-female="/audios/us/awe-us-female.mp3"></span></li><li><span class="pho">vc</span> - <em>add</em> <span class="pho alt">æd</span><span class="speak-word-inline" data-audio-us-male="/audios/us/add-us-male.mp3" data-audio-us-female="/audios/us/add-us-female.mp3"></span></li><li><span class="pho">vcc</span> - <em>east</em> <span class="pho alt">iːst</span><span class="speak-word-inline" data-audio-us-male="/audios/us/east-us-male.mp3" data-audio-us-female="/audios/us/east-us-female.mp3"></span></li><li><span class="pho">vccc</span> - <em>axed</em> <span class="pho alt">ækst</span><span class="speak-word-inline" data-audio-us-male="/audios/us/axed-us-male.mp3" data-audio-us-female="/audios/us/axed-us-female.mp3"></span></li><li><span class="pho">cv</span> - <em>the</em> <span class="pho alt">ðə</span><span class="speak-word-inline" data-audio-us-male="/audios/us/the-us-male.mp3" data-audio-us-female="/audios/us/the-us-female.mp3"></span></li><li><span class="pho">cvc</span> - <em>dog</em> <span class="pho alt">dɑːɡ</span><span class="speak-word-inline" data-audio-us-male="/audios/us/dog-us-male.mp3" data-audio-us-female="/audios/us/dog-us-female.mp3"></span></li><li><span class="pho">cvcc</span> - <em>task</em> <span class="pho alt">tæsk</span><span class="speak-word-inline" data-audio-us-male="/audios/us/task-us-male.mp3" data-audio-us-female="/audios/us/task-us-female.mp3"></span></li><li><span class="pho">cvccc</span> - <em>text</em> <span class="pho alt">tekst</span><span class="speak-word-inline" data-audio-us-male="/audios/us/text-us-male.mp3" data-audio-us-female="/audios/us/text-us-female.mp3"></span></li><li><span class="pho">ccv</span> - <em>flee</em> <span class="pho alt">fliː</span><span class="speak-word-inline" data-audio-us-male="/audios/us/flee-us-male.mp3" data-audio-us-female="/audios/us/flee-us-female.mp3"></span></li><li><span class="pho">ccvc</span> - <em>speed</em> <span class="pho alt">spiːd</span><span class="speak-word-inline" data-audio-us-male="/audios/us/speed-us-male.mp3" data-audio-us-female="/audios/us/speed-us-female.mp3"></span></li><li><span class="pho">ccvcc</span> - <em>thrust</em> <span class="pho alt">θrʌst</span><span class="speak-word-inline" data-audio-us-male="/audios/us/thrust-us-male.mp3" data-audio-us-female="/audios/us/thrust-us-female.mp3"></span></li><li><span class="pho">ccvccc</span> - <em>flexed</em> <span class="pho alt">flekst</span><span class="speak-word-inline" data-audio-us-male="/audios/us/flexed-us-male.mp3" data-audio-us-female="/audios/us/flexed-us-female.mp3"></span></li><li><span class="pho">cccv</span> - <em>spray</em> <span class="pho alt">spreɪ</span><span class="speak-word-inline" data-audio-us-male="/audios/us/spray-us-male.mp3" data-audio-us-female="/audios/us/spray-us-female.mp3"></span></li><li><span class="pho">cccvc</span> - <em>spring</em> <span class="pho alt">sprɪŋ</span><span class="speak-word-inline" data-audio-us-male="/audios/us/spring-us-male.mp3" data-audio-us-female="/audios/us/spring-us-female.mp3"></span></li><li><span class="pho">cccvcc</span> - <em>splashed</em> <span class="pho alt">splæʃt</span><span class="speak-word-inline" data-audio-us-male="/audios/us/splashed-us-male.mp3" data-audio-us-female="/audios/us/splashed-us-female.mp3"></span></li><li><span class="pho">cccvccc</span> - <em>scrounged</em> <span class="pho alt">skraʊndʒd</span><span class="speak-word-inline" data-audio-us-male="/audios/us/scrounged-us-male.mp3" data-audio-us-female="/audios/us/scrounged-us-female.mp3"></span></li></ul></blockquote>',8),u=[p];function o(t,i,c,m,d,r){return e(),s("div",null,u)}const g=a(n,[["render",o]]);export{f as __pageData,g as default};
