import{_ as c,c as i,j as n,a as t,a5 as h,o as e}from"./chunks/framework.GA2e1Mwe.js";const V=JSON.parse('{"title":"Ngoài dự đoán","description":"","frontmatter":{},"headers":[],"relativePath":"training-tasks/surprise.md","filePath":"training-tasks/surprise.md"}'),o={name:"training-tasks/surprise.md"},a=h('<h1 id="ngoai-du-đoan" tabindex="-1">Ngoài dự đoán <a class="header-anchor" href="#ngoai-du-đoan" aria-label="Permalink to &quot;Ngoài dự đoán&quot;">​</a></h1><p>Sau một thời gian tự luyện tập, bạn có thể gặp phải một tình huống ngoài dự đoán:</p><blockquote><p>Không biết nói gì...</p></blockquote><p>Ban đầu, 3 giờ mỗi ngày của bạn nên tập trung vào <strong>hình thành giọng nói</strong> và <strong>mở rộng trí nhớ</strong>, nhưng nhiều người phát hiện rằng &quot;viết ra những gì mình muốn nói bằng tiếng mẹ đẻ&quot; ngày càng tốn thời gian và công sức, cuối cùng, thậm chí không thể nhớ ra điều gì để nói... Chuyện gì đang xảy ra? Đây là một tình huống rất khó phát hiện, nhiều người có thể chưa bao giờ nhận ra. Mặc dù khó khăn trong việc học ngoại ngữ có thể đến từ nhiều khía cạnh khác nhau, điều khiến người ta bối rối nhất là nhận ra khó khăn lớn nhất lại đến từ trình độ tiếng mẹ đẻ của chính mình.</p><p>Rõ ràng, trình độ tiếng mẹ đẻ giới hạn việc tiếp thu ngôn ngữ thứ hai.</p><p>Một số người ban đầu nghĩ rằng vấn đề của mình là do từ vựng không đủ... Vì vậy, họ tìm đến sách từ vựng hoặc phần mềm học từ để học. Mọi người đều giống nhau, hiệu quả không tốt lắm. Tại sao? Vì có quá nhiều từ là những thứ mình không dùng trong cuộc sống, những khái niệm mà mình thậm chí chưa từng gặp trong tiếng mẹ đẻ, thì làm sao có thể nhớ được khi dùng ngoại ngữ? Ghi nhớ không phải là không thể, thậm chí còn tốt, nhưng dùng vào đâu mới là vấn đề?</p><p>Đây là một sự thật rất đáng tiếc:</p>',7),g={class:"MathJax",jax:"SVG",style:{direction:"ltr",position:"relative"}},r={style:{overflow:"visible","min-height":"1px","min-width":"1px","vertical-align":"-0.816ex"},xmlns:"http://www.w3.org/2000/svg",width:"1.795ex",height:"2.773ex",role:"img",focusable:"false",viewBox:"0 -864.9 793.6 1225.5","aria-hidden":"true"},s=h('<g stroke="currentColor" fill="currentColor" stroke-width="0" transform="scale(1,-1)"><g data-mml-node="math"><g data-mml-node="mfrac"><g data-mml-node="mn" transform="translate(220,394) scale(0.707)"><path data-c="31" d="M213 578L200 573Q186 568 160 563T102 556H83V602H102Q149 604 189 617T245 641T273 663Q275 666 285 666Q294 666 302 660V361L303 61Q310 54 315 52T339 48T401 46H427V0H416Q395 3 257 3Q121 3 100 0H88V46H114Q136 46 152 46T177 47T193 50T201 52T207 57T213 61V578Z" style="stroke-width:3;"></path></g><g data-mml-node="mn" transform="translate(220,-345) scale(0.707)"><path data-c="33" d="M127 463Q100 463 85 480T69 524Q69 579 117 622T233 665Q268 665 277 664Q351 652 390 611T430 522Q430 470 396 421T302 350L299 348Q299 347 308 345T337 336T375 315Q457 262 457 175Q457 96 395 37T238 -22Q158 -22 100 21T42 130Q42 158 60 175T105 193Q133 193 151 175T169 130Q169 119 166 110T159 94T148 82T136 74T126 70T118 67L114 66Q165 21 238 21Q293 21 321 74Q338 107 338 175V195Q338 290 274 322Q259 328 213 329L171 330L168 332Q166 335 166 348Q166 366 174 366Q202 366 232 371Q266 376 294 413T322 525V533Q322 590 287 612Q265 626 240 626Q208 626 181 615T143 592T132 580H135Q138 579 143 578T153 573T165 566T175 555T183 540T186 520Q186 498 172 481T127 463Z" style="stroke-width:3;"></path></g><rect width="553.6" height="60" x="120" y="220"></rect></g></g></g>',1),l=[s],u=n("mjx-assistive-mml",{unselectable:"on",display:"inline",style:{top:"0px",left:"0px",clip:"rect(1px, 1px, 1px, 1px)","-webkit-touch-callout":"none","-webkit-user-select":"none","-khtml-user-select":"none","-moz-user-select":"none","-ms-user-select":"none","user-select":"none",position:"absolute",padding:"1px 0px 0px 0px",border:"0px",display:"block",width:"auto",overflow:"hidden"}},[n("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[n("mfrac",null,[n("mn",null,"1"),n("mn",null,"3")])])],-1),m={class:"MathJax",jax:"SVG",style:{direction:"ltr",position:"relative"}},p={style:{overflow:"visible","min-height":"1px","min-width":"1px","vertical-align":"-0.816ex"},xmlns:"http://www.w3.org/2000/svg",width:"3.395ex",height:"2.773ex",role:"img",focusable:"false",viewBox:"0 -864.9 1500.7 1225.5","aria-hidden":"true"},d=h('<g stroke="currentColor" fill="currentColor" stroke-width="0" transform="scale(1,-1)"><g data-mml-node="math"><g data-mml-node="mfrac"><g data-mml-node="mn" transform="translate(573.6,394) scale(0.707)"><path data-c="31" d="M213 578L200 573Q186 568 160 563T102 556H83V602H102Q149 604 189 617T245 641T273 663Q275 666 285 666Q294 666 302 660V361L303 61Q310 54 315 52T339 48T401 46H427V0H416Q395 3 257 3Q121 3 100 0H88V46H114Q136 46 152 46T177 47T193 50T201 52T207 57T213 61V578Z" style="stroke-width:3;"></path></g><g data-mml-node="mn" transform="translate(220,-345) scale(0.707)"><path data-c="31" d="M213 578L200 573Q186 568 160 563T102 556H83V602H102Q149 604 189 617T245 641T273 663Q275 666 285 666Q294 666 302 660V361L303 61Q310 54 315 52T339 48T401 46H427V0H416Q395 3 257 3Q121 3 100 0H88V46H114Q136 46 152 46T177 47T193 50T201 52T207 57T213 61V578Z" style="stroke-width:3;"></path><path data-c="30" d="M96 585Q152 666 249 666Q297 666 345 640T423 548Q460 465 460 320Q460 165 417 83Q397 41 362 16T301 -15T250 -22Q224 -22 198 -16T137 16T82 83Q39 165 39 320Q39 494 96 585ZM321 597Q291 629 250 629Q208 629 178 597Q153 571 145 525T137 333Q137 175 145 125T181 46Q209 16 250 16Q290 16 318 46Q347 76 354 130T362 333Q362 478 354 524T321 597Z" transform="translate(500,0)" style="stroke-width:3;"></path><path data-c="30" d="M96 585Q152 666 249 666Q297 666 345 640T423 548Q460 465 460 320Q460 165 417 83Q397 41 362 16T301 -15T250 -22Q224 -22 198 -16T137 16T82 83Q39 165 39 320Q39 494 96 585ZM321 597Q291 629 250 629Q208 629 178 597Q153 571 145 525T137 333Q137 175 145 125T181 46Q209 16 250 16Q290 16 318 46Q347 76 354 130T362 333Q362 478 354 524T321 597Z" transform="translate(1000,0)" style="stroke-width:3;"></path></g><rect width="1260.7" height="60" x="120" y="220"></rect></g></g></g>',1),T=[d],k=n("mjx-assistive-mml",{unselectable:"on",display:"inline",style:{top:"0px",left:"0px",clip:"rect(1px, 1px, 1px, 1px)","-webkit-touch-callout":"none","-webkit-user-select":"none","-khtml-user-select":"none","-moz-user-select":"none","-ms-user-select":"none","user-select":"none",position:"absolute",padding:"1px 0px 0px 0px",border:"0px",display:"block",width:"auto",overflow:"hidden"}},[n("math",{xmlns:"http://www.w3.org/1998/Math/MathML"},[n("mfrac",null,[n("mn",null,"1"),n("mn",null,"100")])])],-1),_=h('<p>Nhưng vấn đề không phải ở từ vựng, không phải ở ngữ pháp, thậm chí không phải ở khả năng ngôn ngữ cơ bản, mà là do thiếu <strong>khả năng quan sát</strong> và <strong>khả năng suy nghĩ</strong>. Bộ não cần đầu vào sau đó xử lý để xuất ra. Giống như máy tính, nếu đầu vào là rác, đầu ra cũng chỉ là rác. Nếu không có đầu vào, thì xử lý cái gì đây? Nếu xử lý không rõ ràng, thì xuất ra có ích gì?</p><p>Tuy nhiên, cũng đừng lo lắng, thực sự có cách để nhanh chóng cải thiện, vì cấu trúc ngôn ngữ hàng ngày rất đơn giản.</p><p>Nếu là <strong>lý luận</strong>, thì:</p><blockquote><p>Phổ biến nhất là sau khi nói một câu, chọn một trong ba khía cạnh <em>What</em>, <em>Why</em>, <em>How</em> để mở rộng, nếu cần thiết thì mở rộng toàn diện từng khía cạnh...</p></blockquote><p>Nói cách khác, nó giống như một cái bàn, có mặt bàn, sau đó có thể chỉ có một chân (một ví dụ, một lý do, một giải thích), cũng có thể có hai chân (một ví dụ, thêm một lý do; hoặc hai ví dụ; hoặc hai lý do), cũng có thể có ba chân, bốn chân... Nhưng thông thường không cần quá nhiều chân...</p><p>Nếu là <strong>kể chuyện</strong>, thì:</p><blockquote><p>Phổ biến nhất là kể theo <strong>trình tự thời gian</strong>, nếu không thì dùng <strong>hồi tưởng</strong> hoặc <strong>chèn kể</strong> – để tạo ra hồi hộp và hiệu ứng.</p></blockquote><p>Ngoài ra, tất cả đều là <strong>chi tiết</strong>. Khả năng cần thiết chỉ có thể dựa vào ngày càng tích lũy. Nhìn thấy từ ngữ hay, câu hay, kiểu câu hay thì lưu giữ lại; nhìn thấy tình tiết hay, đoạn văn hay thì lưu giữ lại; nhìn thấy ví dụ hay, phân tích hay, cấu trúc hay thì lưu giữ lại... Chỉ cần gặp cơ hội có thể áp dụng thì cố gắng dùng nhiều lần – chỉ có vậy thôi.</p><p>Nếu bạn muốn tạo thói quen, hãy xử lý thêm chi tiết, chẳng hạn:</p><blockquote><ul><li>Thông tin chính xác: con số, năm tháng, tên người, tên địa danh;</li><li>Nếu có trích dẫn, văn bản phải không sai một chữ;</li></ul></blockquote><p>Như vậy, khả năng xuất ra của bạn sẽ cơ bản đạt yêu cầu... Chỉ cần bắt đầu, liên tục tích lũy, sẽ ngày càng tốt hơn. Bạn thấy đấy, nhiều người chỉ vì quen lười biếng thôi – ban đầu không phải không làm được, nhưng lười quá lâu, thì không thể làm được nữa.</p><p><strong>Xuất ra</strong> (nói, viết) thì tiền đề là <strong>xử lý</strong> (suy nghĩ)? Nó thực ra dựa vào tiền đề khác – <strong>đầu vào</strong> (đọc, quan sát). Vì chỉ cần có đầu vào là cần xử lý, nên đầu vào càng nhiều, kinh nghiệm xử lý càng phong phú, đơn giản vậy thôi. Vì vậy, nhất định phải đọc nhiều sách, đọc sách hay, đó cũng là hoạt động cần thiết để giữ cho não bộ khỏe mạnh hàng ngày.</p><p>Ngoài ra, quan trọng nhất là tạo thói quen:</p><blockquote><p><strong>Thu thập các câu hỏi đáng suy nghĩ</strong>.</p></blockquote><p>Chuẩn bị một cuốn sổ riêng cho việc này, hoặc một mục riêng trong ứng dụng ghi chú trên điện thoại. Liên tục thu thập, liên tục sắp xếp. Nếu không có câu hỏi trong đầu, bộ não sẽ không đặc biệt xử lý các đầu vào liên quan. Nếu trong đầu đầy câu hỏi, bạn sẽ phát hiện mình quan sát nhiều hơn, khi quan sát sẽ thấy nhiều chi tiết hơn, khi suy nghĩ sẽ liên kết nhiều hơn...</p><p>Bộ <strong>câu hỏi</strong> này, một mặt giống như nam châm, thu hút những thông tin mà nếu không có nó bạn có thể không chú ý đến, mặt khác giống như cái rây, giúp bạn lọc ra những thứ không liên quan đến bạn. Cuối cùng, bạn sẽ thấy nó là một trong những công cụ tốt nhất để kiểm soát sự chú ý của mình.</p><p>Tất nhiên, nếu có thời gian và năng lượng, bạn nên đọc thêm nhiều sách liên quan đến viết lách. Ví dụ, <a href="https://www.amazon.com/Elements-Style-4th-Feathers-Classics-ebook/dp/B0798TTKDT/" target="_blank" rel="noreferrer">The Elements of Style</a> của William Strunk Jr., <a href="https://www.amazon.com/Sense-Style-Thinking-Persons-Writing-ebook/dp/B00INIYG74/" target="_blank" rel="noreferrer">The Sense of Style: The Thinking Person&#39;s Guide to Writing in the 21st Century</a> của Steven Pinker, <a href="https://www.amazon.com/Style-Lessons-Joseph-M-Williams-ebook/dp/B084BY6R32/" target="_blank" rel="noreferrer">Style: Lessons in Clarity and Grace</a> và <a href="https://www.amazon.com/Research-Chicago-Writing-Editing-Publishing-ebook/dp/B01L0HWJ6E/" target="_blank" rel="noreferrer">The Craft of Research</a> của Joseph M. Williams, <a href="https://www.amazon.com/Writing-Memoir-Craft-Stephen-King-ebook/dp/B000FC0SIM/" target="_blank" rel="noreferrer">On Writing: A Memoir Of The Craft</a> của Stephen King, <a href="https://www.amazon.com/Story-Structure-Substance-Principles-Screenwriting-ebook/dp/B0042FZVOY/" target="_blank" rel="noreferrer">Story: Style, Structure, Substance, and the Principles of Screenwriting</a> của Robert McKee... Có rất nhiều sách hay.</p>',17);function b(y,Q,x,v,w,f){return e(),i("div",null,[a,n("blockquote",null,[n("p",null,[t("Ở bất kỳ quốc gia nào, mặc dù giao tiếp cơ bản trong cuộc sống không có vấn đề gì, nhưng tỷ lệ người có thể sử dụng ngôn ngữ một cách tốt để diễn đạt và giao tiếp luôn dưới "),n("mjx-container",g,[(e(),i("svg",r,l)),u]),t("...")])]),n("p",null,[t("Đây thực ra là nói giảm. Nếu nâng yêu cầu lên một chút, nói là "),n("mjx-container",m,[(e(),i("svg",p,T)),k]),t(" cũng không quá. Bạn chỉ cần nhìn xung quanh có bao nhiêu người có thể đứng lên diễn thuyết, công khai viết bài thì sẽ biết, và trong số ít đó, phần lớn chỉ là những người không thực sự xuất sắc...")]),_])}const q=c(o,[["render",b]]);export{V as __pageData,q as default};
